import React, { Component } from "react";
import "../Styles/Home.css";

class Home extends Component {
  constructor(props) {
    super(props);
    this.state = {
      isSearch: false,
      vehicle: "",
      from: "",
      to: "",
      date:'',
    };
  }
  openSearchDilog = () => {
    this.setState({ isSearch: true });
  };
  closeSearchDilog = () => {
    this.setState({ isSearch: false });
  };

  handleInputChange = (event) => {
    const { className, value } = event.target;
    this.setState({ [className]: value });
  };


  render() {
    const { isSearch } = this.state;
    return (
      <div id="home">
        <div className="home-container slideshow">
          <div className="container-hero">
            <div className="hero">
              <div className="hero-text">
                <h1>Welcome to blah</h1>
                <h2>Leading the way in Adventure</h2>
                <h3>
                  We believe everyone should travel Because travellingâ€™s for
                  everyone. We make vacations magical Making life the daring
                  adventure you deserve.
                </h3>
                <button>CONTACT US</button>
              </div>
            </div>
          </div>
          <div className="container-hero2">
            <div className="detail">
              <select class="vehicle" onChange={this.handleInputChange}>
                <option value="SUV">SUV</option>
                <option value="Jeep">Jeep</option>
                <option value="Cab">Cab</option>
                <option value="Bike">Bike</option>
              </select>
              <input
                type="text"
                className="location"
                placeholder="From"
                onChange={this.handleInputChange}
              />

              <input
                type="text"
                className="location"
                placeholder="To"
                onChange={this.handleInputChange}
              />
              <input
                type="date"
                className="date"
                placeholder="Time"
                onChange={this.handleInputChange}
              />
              <button className="search-button" onClick={this.openSearchDilog}>
                Search
              </button>
              {isSearch && (
                <div className="overlay">
                  <div className="dialog">
                    <p>Vehicle:{this.state.vehicle}</p>
                    <p>From:{this.state.from}</p>
                    <p>To:{this.state.to}</p>
                    <p>Date:{this.state.date}</p>
                    <button onClick={this.closeSearchDilog}>Close</button>
                  </div>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default Home;
