import React, { Component, useState } from "react";
import "../Styles/Home.css";

class Home extends Component {
constructor(props){
  super(props);
  this.state = {
    isSearch : false
  }
}
openSearchDilog = () => {
  this.setState({isSearch:true});
}
closeSearchDilog = () => {
  this.setState(
    {isSearch:false}
  )
}

  render() {
    const {isSearch} = this.state
    return (
      <div id="home">
        <div className="home-container slideshow">
          <div className="container-hero">
            <div className="hero">
              <div className="hero-text">
                <h1>Welcome to blah</h1>
                <h2>Leading the way in Adventure</h2>
                <h3>
                  We believe everyone should travel Because travellingâ€™s for
                  everyone. We make vacations magical Making life the daring
                  adventure you deserve.
                </h3>
                <button>CONTACT US</button>
              </div>
            </div>
          </div>
          <div className="container-hero2">
            <div className="detail">
              <select class="vehicle">
                <option value="option1">SUV</option>
                <option value="option2">Jeep</option>
                <option value="option3">Cab</option>
                <option value="option4">Bike</option>
              </select>
              <input type="text" className="location" placeholder="From" />

              <input type="text" className="location" placeholder="To" />
              <input type="date" className="date" placeholder="Time" />
              <button className="search-button" onClick={this.openSearchDilog}>Search</button>
              {isSearch && (
          <div className="overlay">
            <div className="dialog">
              <p>This is a popup dialog!</p>
              <button onClick={this.closeDialog}>Close</button>
            </div>
          </div>
        )}
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default Home;
